INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Dimethicone Crosspolymer","Dimeticona Crosspolímero","Um elastômero de silicone de alto peso molecular (material elástico semelhante a borracha) que geralmente é misturado com um fluido de silicone de base (como dimeticona ou ciclopentasiloxano) para dar à fórmula uma sensação suave e sedosa e para atuar como um agente espessante."," emulsion stabilising, hair fixing, viscosity controlling
  ");
