INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Alpha-Glucan Oligosaccharide","Oligossacarídeo alfa-glucano","Um ingrediente protetor da pele obtido por síntese enzimática a partir de açúcares naturais (sacarose e maltose). Alega-se ser um substrato biosseletivo que protege e estimula a flora microbiana benéfica da pele sem fazer o mesmo com patógenos e flora indesejável. Também estimula a liberação de peptídeos antimicrobianos pelos queratinócitos (células da pele)."," cleansing, skin conditioning, smoothing
  ");
