INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Polyglyceryl-3 Methylglucose Distearate","Poligliceril-3 Metilglicose Diestearato","Um emulsificante à base de vegetais que ajuda as partes oleosas e aquosas da fórmula a se misturarem bem. É compatível com vários óleos cosméticos, bem como ingredientes ativos e sua especialidade é criar emulsões com alta estabilidade ao calor e ao congelamento (de -25 °C a +50 °C)."," emulsifying
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Propylene Glycol Dicaprylate/Dicaprate","Propileno Glicol Dicaprilato/Dicaprato","Propylene Glycol Dicaprylate/Dicaprate é um emoliente que deixa uma sensação de pele leve, não oleosa, lisa e aveludada. De acordo com as informações do fabricante, também é ótimo para dispersar e dissolver pigmentos e protetores solares ativos. Na maioria das vezes, você encontrará esse cara em hidratantes leves, protetores solares ou produtos de maquiagem."," emollient
