INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Sodium Cocoyl Sarcosinate","Cocoil Sarcosinato de Sódio","Um agente de limpeza suave e suave derivado do aminoácido sarcosina e ácidos graxos de coco. É conhecido por suas boas habilidades de aumentar a espuma e melhorar a suavidade da fórmula."," cleansing, hair conditioning, surfactant
