INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Cetearyl Isononanoate","Isononanoato de cetearil","É um óleo límpido e levemente amarelado (vem do óleo de coco ou palmiste) que é usado como um emoliente tradicional de espalhamento médio."," emollient, hair conditioning, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Butyloctyl Salicylate","Salicilato estilo garrafa","Um bom ingrediente auxiliar multifuncional que é especialmente útil em protetores solares. Ele pode solubilizar alguns filtros UV comumente usados como Oxybenzone ou Avobenzone e também pode ajudar a aumentar a classificação SPF dos protetores solares. Também é cosmeticamente elegante, tem excelente espalhabilidade e uma sensação agradável e hidratante na pele. Ah, e de acordo com a Wikipedia, até ajuda a estabilizar o famoso filtro UVA instável, Avobenzona."," hair conditioning, skin conditioning, solvent
  ");
