INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Sodium C14-16 Olefin Sulfonate","Sulfonato de olefina C14-16 de sódio","Um agente de limpeza versátil e biodegradável com alto poder de limpeza e fortes propriedades de formação de espuma. Infelizmente, essas duas propriedades de um surfactante geralmente significam que ele é agressivo para a pele, o que também é o caso aqui."," cleansing, foaming, surfactant
  ");
