INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Pyrus Malus Fruit Extract","Extrato de Fruta Pyrus Malus","A maçã dispensa apresentações como uma das frutas mais comuns do planeta Terra. Não é apenas um lanche de frutas saudável, mas também uma guloseima se você colocar em todo o rosto. É carregado com proteínas, amido, açúcares, ácidos, vitaminas e sais. Os açúcares (principalmente frutose, glicose, sacarose) conferem ao extrato de maçã boas propriedades hidratantes e suavizantes, enquanto os ácidos (principalmente ácido málico e gálico) conferem propriedades esfoliantes suaves, clareadoras da pele e antibacterianas."," skin conditioning
  ");
