INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Caramel","Caramelo","O caramelo dos cosméticos é praticamente o mesmo que você conhece da cozinha. É derivado do tratamento térmico controlado de carboidratos de qualidade alimentar (açúcares) e funciona como um corante marrom."," cosmetic colorant, masking
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Macadamia Ternifolia Seed Oil","Óleo de Semente de Macadâmia Ternifolia","O óleo amarelo dourado proveniente da noz macadâmia, uma noz nativa australiana. Semelhante a outros óleos vegetais, é carregado com ácidos graxos emolientes e nutritivos. É um óleo com alto teor de ácido oleico (50-67% de ácido oleico e apenas 0-5% de ácido linoleico) que o torna muito emoliente e ideal para tipos de pele seca (e menos ideal para pele propensa a acne). contém grandes quantidades de um ácido graxo raro chamado ácido palmitoleico (12-25%) que dá ao óleo de macadâmia uma sensação de almofada. Também é facilmente absorvido e torna a pele macia e flexível."," emollient
  ");
