INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Aloe Barbadensis Leaf Extract","Extrato de Folha de Aloe Barbadensis","O extrato proveniente do suco contendo folhas da planta Aloe vera. Geralmente é um extrato hidroglicólico (embora também exista um extrato de óleo para as partes lipídicas) que tem propriedades hidratantes, emolientes e anti-inflamatórias semelhantes às do próprio suco. Nós escrevemos um pouco mais sobre aloe aqui."," emollient, humectant, oral care, skin conditioning
  ");
