INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Sorbic Acid","Ácido sórbico","Um conservante natural suave que geralmente vem para a fórmula junto com seus outros amigos conservantes suaves, como Ácido Benzoico e/ou Ácido Desidroacético. Aliás, também é usado como conservante de alimentos."," preservative
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Tetrahydrodiferuloylmethane","Tetrahidrodiferuloilmetano","Um extrato em pó padronizado das raízes da planta Curcuma longa (cúrcuma) (juntamente com este e este). O trio é chamado Tetrahidrocurcuminóides (THC) e tem propriedades mágicas de clareamento da pele, anti-inflamatórias e antioxidantes.
Outros extratos de açafrão têm uma cor amarela brilhante que os torna difíceis de usar, mas este é incolor."," antioxidant
  ");
