INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Hydroxycitronellal","Hidroxicitronelal","Um ingrediente de fragrância comum que tem um aroma doce em algum lugar entre o lírio e o melão frutado. Pode ser encontrado em óleos essenciais, como óleo de lavanda, óleo de flor de laranjeira ou ylang-ylang. Em cosméticos, pode ser usado até 1%. É uma das “fragrâncias da UE 26” que deve ser rotulada separadamente (e não pode ser simplesmente incluída no termo “fragrância/perfume” no rótulo) devido ao potencial alergênico. Melhor evitar se sua pele for sensível."," perfuming
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Oligopeptide-34","Oligopeptídeo-34",""," skin conditioning
