INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Isopentyldiol","isopentildiol","Um líquido umectante solúvel em água, incolor e inodoro que torna sua pele agradável e macia (também conhecida como emoliente) e dá uma sensação de pele seca e não pegajosa. Ele também tem um ótimo efeito hidratante instantâneo e de longa duração e funciona em sinergia com o outro hidratante, sorbitol."," solvent
  ");
