INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Disodium Lauriminodipropionate Tocopheryl Phosphates","Dissódio Lauriminodipropionato Tocoferol Fosfatos","Um derivado biodisponível do famoso antioxidante, a Vitamina E, que reduz a aparência de vermelhidão da pele causada por várias agressões externas à pele, como acne, queimaduras de barbear ou luz ultravioleta."," antistatic, cleansing, foaming, hair conditioning, hair fixing, skin conditioning, surfactant
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Lauryl Glucoside","Lauril glicosídeo","De origem 100% vegetal, biodegradável, agente de limpeza suave que fornece espuma de moderada a alta. É um prazer trabalhar em conjunto com outros surfactantes (em geral, isso ajuda a criar fórmulas mais suaves)."," cleansing, surfactant
  ");
