INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Dehydroacetic Acid","Ácido Desidroacético","Um ingrediente auxiliar que ajuda a manter os produtos bonitos por mais tempo, também conhecido como conservante. Atua principalmente contra fungos e tem efeito apenas mais brando contra bactérias. É aprovado pela Ecocert e Cosmos, funciona muito bem em baixas concentrações (0,1-0,6%) e é popular em produtos naturais."," preservative
  ");
