INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Ethyl Oleate","Etil Oleato","É um Éster Etílico da Vitamina F, que é usado como emoliente. Juntamente com outras duas formas de éster etílico da vitamina F (linoleato de etila e linolenato de etila), o trio destina-se a tratar a pele seca e seborreica com barreira lipídica danificada e couro cabeludo seco com cabelos quebradiços e sem brilho."," emollient, perfuming
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Lactobacillus/Punica Granatum Fruit Ferment Extract","Lactobacillus/Punica Extrato de Fermento de Fruta de Romã","É o nome sofisticado da enzima da romã obtida pela fermentação da fruta da romã com Lactobacillus lactis. O processo de fermentação torna os fitoquímicos desejados prontamente disponíveis e técnicas de filtração seletiva são usadas para isolar as frações proteolíticas.
O resultado final é a enzima que aumenta a renovação celular e pode ser uma alternativa à esfoliação ácida (embora não seja tão eficaz quanto o ácido glicólico, mesmo de acordo com as medições do fabricante sem falar dos outros benefícios do ácido glicólico)."," hair conditioning, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Crambe Abyssinica Seed Oil","Óleo de Semente de Crambe Abyssinica","O óleo vegetal emoliente proveniente das sementes de Crambe Abyssinica, uma planta não muito conhecida nativa das Terras Altas da Etiópia. Possui um perfil único de ácidos graxos e contém grandes quantidades de ácido erúcico (50-65%), um ácido graxo insaturado de 22 carbonos. O óleo é descrito como tendo uma sensação de pele elegante, sedosa e almofadada, semelhante a silicone. Também é ideal para cuidar dos cabelos, pois proporciona deslizamento, maciez e brilho aos cabelos."," hair conditioning, skin conditioning
  ");
