INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Biosaccharide Gum-2","Biossacarídeo Goma-2","Um interessante polissacarídeo (uma grande molécula de açúcar) obtido por fermentação bacteriana. Sua sequência de açúcares é rica em ramnose, galactose e ácido glicurônico. Segundo seu fabricante, o Biosaccharide Gum-2 é um agente protetor contra os danos dos agressores externos que aceleram o envelhecimento da pele e é um potenciador do conforto da pele estimulando a liberação de moléculas de prazer."," skin conditioning
