INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Hydroxyphenyl Propamidobenzoic Acid","Ácido Hidroxifenil Propamidobenzóico","Uma molécula anti-irritante e anti-coceira que copia o ingrediente ativo (Avenantramidas) na conhecida planta calmante, a aveia. De acordo com o estudo clínico do fabricante (duplo-cego, 40 pessoas, 4 semanas de duração), 2% SymCalmin (o nome comercial da nossa molécula diluída - 5%) reduziu significativamente a coceira em 65% e também reduziu a vermelhidão em 50%."," skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Caesalpinia Spinosa Fruit Extract","Extrato de fruta Caesalpinia Spinosa",""," skin protecting
