INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Hydrolyzed Wheat Protein","Proteína de Trigo Hidrolisada","Uma versão quimicamente cortada da proteína do trigo que consiste principalmente em aminoácidos (os blocos de construção), peptídeos (alguns aminoácidos juntos) e proteínas (muitos aminoácidos juntos). Tem propriedades hidratantes e formadoras de filme e  pode ser capaz de neutralizar os efeitos irritantes de agentes de limpeza em produtos de limpeza e xampus. Também pode condicionar e reparar cabelos danificados, deixando-os macios, sedosos e macios."," antistatic, hair conditioning, skin conditioning
