INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Triticum Vulgare Germ Extract","Extrato de germe Triticum Vulgare","É um extrato vegetal que vem do gérmen de trigo. Usado como condicionador de pele e cabelo e protetor de pele. Contém glúten, se você é alérgico."," skin conditioning, skin protecting
