INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Spiraea Ulmaria Extract","Extrato de Spiraea Ulmaria","O extrato proveniente das folhas e/ou flores de Meadowsweet, uma planta nativa da Europa que gosta de crescer em locais úmidos, como prados ou margens de riachos. Seu principal cuidado com a pele é que contém derivados de ácido salicílico que dão a extrair propriedades anti-inflamatórias. Também possui taninos que tornam adstringente o Extrato de Spiraea Ulmaria, bem como espiraeosídeo que possui propriedades anticelulíticas."," astringent, skin conditioning, tonic
  ");
