INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Aspartic Acid","Ácido Aspártico","Um aminoácido não essencial  (importante bloco de construção do colágeno e da elastina) que hidrata a pele. Também é usado para definir o pH do produto cosmético (tampão)."," antistatic, hair conditioning, masking, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Valine","valina","Um aminoácido essencial (um bloco de construção das proteínas da pele, como o colágeno) que nosso corpo não pode produzir sozinho, mas precisa obter de fontes externas, como a dieta.
É um aminoácido de cadeia ramificada que supostamente aumenta a energia, aumenta a resistência e ajuda na recuperação e reparo do tecido muscular quando tomado como suplemento. Não está claro o que a valina faz quando você a coloca na pele, mas como todos os aminoácidos, deve ser pelo menos um ótimo hidratante para a pele."," antistatic, hair conditioning, masking, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Histidine","Histidina","Um aminoácido semi-essencial, o que significa que deve ser ingerido o suficiente para que o corpo não use aminoácidos essenciais (que nosso corpo não pode produzir sozinho) para sintetizá-lo. Tem um papel importante na regulação da defesa imunológica, reações alérgicas e processos inflamatórios no corpo. Quanto aos cuidados com a pele, é um hidratante para a pele que também pode proteger de algumas infecções cutâneas."," antistatic, humectant, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Phenylalanine","fenilalanina","Um aminoácido essencial que o corpo não consegue produzir sozinho, mas precisa retirar da dieta. Combinada com a exposição aos raios UVA, a fenilalanina é usada no tratamento do vitiligo (um distúrbio de pigmentação em que manchas da pele perdem o pigmento)."," antistatic, humectant, skin conditioning
  ");
