INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Lauryl PEG-9 Polydimethylsiloxyethyl Dimethicone","Lauril PEG-9 Polidimetilsiloxietil Dimeticona","O rapidamente chamado Lauryl Peg-9 Polidimetilsiloxietil Dimeticona é um fluido emulsificante de silicone cujo principal objetivo é ser um bom dispersante para pós como filtros UV minerais ou pigmentos coloridos. De acordo com o fabricante Kobo, esta forma de dimeticona parcialmente solúvel em água e parcialmente oleosa ajuda filtros como o dióxido de titânio e o óxido de zinco a se dispersarem de maneira mais uniforme e, assim, fornecem melhor proteção UV, ao mesmo tempo em que minimizam qualquer mancha branca que muitas vezes vem de mãos dadas com filtros minerais. Por isso também pode ser encontrado em produtos de maquiagem como sombras e corretivos, pois ajuda a dispersar os pigmentos de cor da mesma forma."," hair conditioning, skin conditioning, surfactant
  ");
