INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Phytosteryl Macadamiate","Fitosteril Macadamiato","Phytosteryl Macadamiate é a combinação de fitoesteróis e ácidos graxos provenientes do óleo de macadâmia. É uma versão botânica dos ésteres de colesterol encontrados nos lipídios da superfície da pele. Ele chega à lista de ingredientes como parte do L22, um mimético lipídico da superfície da pele cuja principal função é reparar a barreira e hidratar a pele."," hair conditioning, skin conditioning
  ");
INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Sodium Lauryl Sulfoacetate","Lauril Sulfoacetato de Sódio","Um agente de limpeza de origem natural e prontamente biodegradável que fornece uma espuma rica e cremosa, é mais suave para a pele do que a maioria dos limpadores à base de sulfato e proporciona uma sensação luxuosa e sedosa à pele."," cleansing, foaming, surfactant
