INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Tetrasodium Tetracarboxymethyl Naringeninchalcone","Naringeninchalcona tetrassódica Tetracarboximetil Naringeninchalcona","Uma molécula estabilizada solúvel em água derivada do polifenol naringenina chalcona, que ocorre naturalmente na casca de frutas cítricas e na pele do tomate e é conhecida por suas potentes propriedades anti-alérgicas e anti-inflamatórias. De acordo com o estudo clínico do fabricante suíço com 11 pessoas, uma emulsão contendo 0,1% Tetracarboximetil Naringeninchalcona aplicada duas vezes ao dia por 56 dias reduz visivelmente a vermelhidão da pele e melhora os sinais de rosácea na pele."," skin conditioning
