INSERT INTO Item (item_name,item_name_pt,item_description,item_function) VALUES ("Saccharomyces Ferment Filtrate","Filtrado de Fermento Saccharomyces","Saccharomyces Ferment Filtrate é o nome sofisticado de uma coisa líquida, quase aquosa, que você obtém fermentando e filtrando o fermento. De acordo com as informações do fabricante, é rico em todos os tipos de coisas boas para a pele: minerais essenciais, aminoácidos, beta-glucana e vitaminas. É definitivamente ótimo para hidratação e calmante da pele, e também pode ter alguma atividade mágica de clareamento da pele e reparação de rugas."," humectant
  ");
